function printHTM(){
     //获取打印样式
     var cssArr = new Array();
     //传入样式文件路径名，首字母必须是字符/
     cssArr.push(cuiMinCssPath);
     //获取打印内容
     var printContent = $('#editDiv').html();
     var arr = new Array();
     arr.push(printContent);
     doPrint('A4',2,'项目模块','HTM',arr,cssArr);
}
