/**
 * 初始化查询行为 列表查询行为方法
 * 该方法只支持后台方法有且只有一个参数，且参数类型为对象（vo）
 * 若是多条件查询，需把条件封装在vo中
 * @obj grid对象
 * @pageQuery grid分页信息
 */
function initDatasource(obj, pageQuery) {
	var queryVarName = 'cascadeUser';
	var query = {};
	if(queryVarName !== ''){
		query=cap.getQueryObject(window[queryVarName],pageQuery);
	}
	//初始化查询参数

	//获取查询条件
 	var paramArray = [];
 	paramArray[0] = query;
 	var dwrInvokeParam = cap.getDwrInvokeParam('com.comtop.cap.demo.cascade.oneToOne.entity.CascadeUser','queryVOListByPage',paramArray);
 	//TODO 调用前操作

 	//调用后台查询
 	dwr.TOPEngine.setAsync(true);
	DemoOneToOneListAction.dwrInvoke(dwrInvokeParam,{callback:function(result){
		var returnValueVarName= 'cascadeUserList';
		if(returnValueVarName === ''){
			returnValueVarName = 'returnValueVarName';
		}
  		//TODO 数据设置前操作

  		window[returnValueVarName]=result.list;
  		//设置到数据源
  		if(result.list!=null){
  			obj.setQuery({pageNo:result.pageNo});
  			obj.setDatasource(result.list,result.count); 
      	}else{
      		obj.setDatasource([],0);
      	}
  		//TODO 数据设置后操作

  		//查询条件保存设置
  		var saveQueryData='no';
  		if(saveQueryData=='yes'){
  			cap.setSessionAttribute({'cascadeUser':eval('cascadeUser')});
  		}
  	},
  	errorHandler:function(message, exception){
	   //TODO 后台异常信息回调

	}
  	});
  	dwr.TOPEngine.setAsync(true);
  	//TODO 可自定义设置返回值

}


/*
 * 点击按钮跳转页面 点击按钮跳转页面
 *
 * @param event 当前事件对象
 * @param self 当前button对象
 * @param mark 传入的参数
 */
function addBizObject(event, self, mark) { 
	var pageJumpURL=cap.getforwardURL(demoOneToOneEdit);
	var container=window;
	//TODO  调用后台前处理逻辑

	//带查询条件返回设置
  	var saveQueryData='no';
  	 if(saveQueryData=='yes'){
  		pageJumpURL=cap.buildURL(pageJumpURL,{clearSession:false});
  	}
  	//当打开的页面为新窗口，并且当前window实现了addBizObject_pageJump方法，则调用addBizObject_pageJump方法来打开新页面
  	var _openWindow = window["addBizObject_pageJump"]; 
  	if("location" == "win" && _openWindow && typeof(_openWindow) == "function"){
  		_openWindow(pageJumpURL);
  	}else{
		cap.pageJump(pageJumpURL,"location",container);
  	}
}


//删除行为 删除行为
function deleteRow(){
	//删除前操作

	var gridId="uiid-15834954290185124";
	var gridObj = cui("#"+gridId);
	var selects = gridObj.getSelectedRowData();
	if(selects == null || selects.length == 0){
		cui.alert("请选择要删除的数据。");
		return;
	}
	cui.confirm("确定要删除这"+selects.length+"条数据吗？",{
		onYes:function(){
			var paramArray = [];
			paramArray[0] = selects;
			var dwrInvokeParam = cap.getDwrInvokeParam('com.comtop.cap.demo.cascade.oneToOne.entity.CascadeUser','cascadeDelete',paramArray);
			DemoOneToOneListAction.dwrInvoke(dwrInvokeParam,{callback:function(result){
				//数据刷新前处理


				if(result){
					gridObj.loadData();
				 	cui.message("删除成功！","success");
				}else{
					cui.error("删除失败，请重新操作！");
				}

				//数据刷新后处理

			},
			errorHandler:function(message, exception){
			   //TODO 后台异常信息回调
			   var message ="删除失败,存在异常信息:"+message;

			   cui.message(message);
			}
			});
		}
	});
	//TODO 可自定义设置返回值

}


//查看链接渲染 grid查看链接渲染
function gridViewLinkRender(rd, index, col) { 
	if(rd[col.bindName] == null){
		return;
	}
	var url=cap.getforwardURL(demoOneToOneEdit);
	url = cap.buildURL(url,{'pageMode':'readonly', 'primaryValue':rd['primaryValue']});

	var content= "<a style=\"cursor:pointer\" onclick=\"cap.pageJump('"+url+"','_self',window)\">" + rd[col.bindName] + "</a>";
	//自定义content内容

	return content;
}
