//树节点lazy load（动态加载）节点时的回调 (tree)
function treeLazyReadEvent(node){
	var orgId = node.getData().key;
	var childData = [];
	var datasoure = [];
	dwr.TOPEngine.setAsync(false);
	TreeTestAction.getTreeDatasource(orgId, function(data){
     if(data != ''){
        datasoure = JSON.parse(data);  
      }
   });
	dwr.TOPEngine.setAsync(true);
	if(!$.isEmptyObject(datasoure) || ($.isArray(datasoure) && datasoure.length > 0 )){
   	  node.addChild(datasoure);
   } else {
   	node.addChild([]);
   }
}

