/*
 * 点击事件 tree节点点击事件
 * 
 * 树组件提供有节点单击和双击事件。
 *
 * @param node Node类型，当前的节点对象
 * @param event 当前的点击事件
 */
function treeClick(node,event) { 
 	//获取主页面frame对象
	var frame = window.parent.frames[1];
   //给建设单位名称设置
	frame.cui("#constructionUnitName").setValue(node.dNode.data.title);	
   //调用查询函数
	window.parent.frames[1].queryData();
}

