//保存表单行为 保存表单行为
function saveForm(){
	//校验前操作

	var saveContinue=4;
	if(cap.validateForm()){
		cap.beforeSave();
		//提交数据前操作
	var vFlag = mmReceiptNotice.validateFlag;
	var vFlagValue=""; 
	if(vFlag!=null && vFlag!= undefined && vFlag.length>0){
   		for(var i=0;i<vFlag.length;i++){
        	vFlagValue += vFlag[i]+",";

      	}
   	 if (vFlagValue.length > 0) {
   		vFlagValue = vFlagValue.substr(0, vFlagValue.length - 1);
     }
    }

	mmReceiptNotice.validateFlag = vFlagValue;

		var paramArray = [];
		var paramsStr = 'mmReceiptNotice';
		if(paramsStr != ''){
			paramArray = paramsStr.split(',');
		}
		var aliasEntityId = '';
		aliasEntityId = aliasEntityId != '' ? aliasEntityId :  'com.comtop.tablerelation.entity.MmReceiptNotice';
		var dwrInvokeParam = cap.getDwrInvokeParam(aliasEntityId,'saveItem',paramArray);
		var result;
		dwr.TOPEngine.setAsync(false);
		CopyTableRelationEditPageAction.dwrInvoke(dwrInvokeParam,{callback:function(_result){
			//赋值前操作
			result = _result;
			if(""!="mmReceiptNotice.id"){
				eval("mmReceiptNotice.id=_result");
			}


			//默认保存成功
			var type = 'success';
			var message = '保存成功！';

			cui.message(message, type,{'callback':function(){
				//回调的操作

				if(type != "success"){
					return;
				}
				var pWindow = cap.searchParentWindow("reloadGridData");
				if(pWindow && typeof pWindow["reloadGridData"] === "function"){
					pWindow["reloadGridData"]();
				}
				if(saveContinue==1){
					window.location=window.location;
				}else if(saveContinue==2){
					window.close();
				}else if(saveContinue==4){
					var reloadURL = window.location.href;
					var localURL = window.location.href;
					if(localURL.indexOf("?")>0){
						if(localURL.indexOf("primaryValue")<0){
							reloadURL = localURL +"&primaryValue="+mmReceiptNotice.id; 
						}
					}else{
						reloadURL = localURL +"?primaryValue="+mmReceiptNotice.id; 
					}
					window.location.href=reloadURL;
				}
			}});
    	},
    	errorHandler:function(message, exception){
		   //TODO 后台异常信息回调
		   var type = 'error';
		   var message ="保存失败,存在异常信息:"+message;

		   cui.message(message);
		}
    	});
    	dwr.TOPEngine.setAsync(true);
	}
	//TODO 可自定义设置返回值

}

